 private void ListarAlunos(){
        try {
            AlunosDAO objalunosdao = new AlunosDAO();
            
            DefaultTableModel model = (DefaultTableModel) tabelaAlunos.getModel();
            model.setNumRows(0);
            
            ArrayList<Aluno> lista=objalunosdao.PesquisarAlunos();
            
            for(int num=0;num<lista.size();num++){
               model.addRow(new Object [] {
                    lista.get(num).getId_aluno(),
                    lista.get(num).getNome_aluno(),
                    lista.get(num).getCpf_aluno(),
                    lista.get(num).getIdade_aluno(),
                    lista.get(num).getMensalidade_aluno(),
                    lista.get(num).getMultaporatraso_aluno(),
                    lista.get(num).getEndereco_aluno(),
                    lista.get(num).getTelefone_aluno()
                });          
            }
            
        } catch (Exception erro) {
            JOptionPane.showMessageDialog(null, "Listar Alunos VIEW"+erro);
        }
    }

